import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavDropdown } from 'react-bootstrap0';
import Button from 'react-bootstrap/Button';
import Cookies from 'js-cookie';
import Form from 'react-bootstrap/Form';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function MainPage() {
  const navigate = useNavigate();
  const [cookie, setCookie] = useState('');
  const [search, setSearch] = useState('');

  const navBarStyle = {
    backgroundColor: 'black',
    height: '10vh',
  }

//   const filteredCandidates = candidates.filter(candidate =>
//     candidate.f_name?.toLowerCase().includes(search.toLowerCase())
//   );

  useEffect(() => {
    const name = Cookies.get('name');
    if (name) {
      setCookie(name);
    } else {
      navigate('/');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    Cookies.remove('name');
    navigate('/login');
  };

  return (
    <div className="Container">
      <Navbar expand="lg" className="bg-body-primary"  style={navBarStyle}>
        <Container fluid>
          <Navbar.Brand href="/main" style={{color:'white', fontWeight: 'bold', fontSize: '3.5vh', fontFamily: 'Monospace'}}>FIND-JOBS</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" style={{backgroundColor: 'white'}}/>
          <Navbar.Collapse id="navbarScroll">
            <Nav className="me-auto my-2 my-lg-0" navbarScroll>
            {/* <Form.Group controlId="search">
            <Form.Control
              type="text"
              placeholder="Search employees..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            </Form.Group> 
           
           //Dynamic search*/}  

            {/* <nav class="navbar navbar-light bg-light">
            <form class="form-inline">
                <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
            </form>
            </nav> 
            
            //Static search*/}

<Nav className="ms-auto my-2 my-lg-0" navbarScroll>
  <NavDropdown 
    title={<img src="your-logo-url-here" alt="Logo" style={{ width: "30px", cursor: "pointer" }} />} 
    id="nav-dropdown"
    align="end"
  >
    <NavDropdown.Item href="#jsp">JSP</NavDropdown.Item>
    <NavDropdown.Item href="#jpp">JPP</NavDropdown.Item>
  </NavDropdown>
</Nav>


            </Nav>
            {cookie && (
              <Nav className="ms-auto my-2 my-lg-0 d-flex align-items-center">
                <span className='m-2 p-2 text-center bg-black text-color-white rounded' style={{ color: "white", fontFamily: "cursive" }}>
                  {cookie}
                </span>
                <Button variant="danger" onClick={handleLogout}>
                  Logout
                </Button>
              </Nav>
            )}
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {cookie && (
        <Container className="text-center d-flex align-items-center flex-column justify-content-center" style={{ marginTop: "5%" }}>
          <Container className="text-center">
            <h2>Welcome to Dashboard</h2>
            <Button variant="primary" href="/login" className="mt-3">
              Login
            </Button>
          </Container>
        </Container>
      )}
    </div>
  );
}
